from pytest import mark

from leet_code.decode_string_394 import decode_string


@mark.parametrize("s, expected", [
    ("3[z]2[2[y]pq4[2[jk]e1[f]]]ef", "zzzyypqjkjkefjkjkefjkjkefjkjkefyypqjkjkefjkjkefjkjkefjkjkefef"),
    ("3[a2[c]]", "accaccacc"),
    ("100[leetcode]", ""),
    ("3[a]2[bc]", "aaabcbc"),
    ("2[abc]3[cd]ef", "abcabccdcdcdef")
])
def test_decode_string(s, expected):
    output = decode_string(s)
    assert output == output
